INSERT INTO `page_component` VALUES ('13', '14', '1', null, '{\"id\":13,\"scaffold_id\":1,\"name\":\"form\",\"label\":\"表单有扩张字段\",\"desc\":\"\",\"cate_id\":1,\"template\":\"import React, { PureComponent } from \'react\';\\nimport { connect } from \'dva\';\\nimport { routerRedux } from \'dva/router\';\\nimport {\\n  message,\\n  Form, Input, DatePicker, Select, Button, Card, InputNumber, Radio, Icon, Tooltip,\\n} from \'antd\';\\nimport PageHeaderLayout from \'../../layouts/PageHeaderLayout\';\\nimport styles from \'./preview.less\';\\n\\nconst FormItem = Form.Item;\\nconst { Option } = Select;\\nconst { RangePicker } = DatePicker;\\nconst { TextArea } = Input;\\n\\n@connect(({ valid, loading }) => ({\\n  valid: valid,\\n  submitting: loading.effects[\'valid/add\'],\\n}))\\n@Form.create()\\nexport default class BasicForms extends PureComponent {\\n  componentDidMount(){\\n    let id = 123;\\n    if(id != 0){\\n      this.props.dispatch({\\n        type: \'valid/info\',\\n        payload: {\\n          id: id,\\n        },\\n      });\\n    }\\n  }\\n  handleSubmit = (e) => {\\n    e.preventDefault();\\n    this.props.form.validateFieldsAndScroll((err, values) => {\\n      if (!err) {\\n        this.props.dispatch({\\n          type: \'valid/add\',\\n          payload: values,\\n          callback: () => {\\n            message.success(\'保存成功\');\\n\\n            this.props.dispatch(routerRedux.push(\'/valid/list\'));\\n          }\\n        });\\n      }\\n    });\\n  }\\n\\n  <% for btn in pageData.customBtns %>\\n    <$ btn.key + \'BeforeHandle\' $> = (e) => {\\n      <$ btn.before $>\\n    }\\n    <$ btn.key + \'Handle\' $> = (e) => {\\n      if(!this.<$ btn.key + \'BeforeHandle()\' $>) return ;\\n\\n      <$ btn.cb $>\\n\\n      this.<$ btn.key + \'AfterHandle();\' $>\\n    }\\n    <$ btn.key + \'AfterHandle\' $> = (e) => {\\n      <$ btn.after $>\\n    }\\n  <% endfor %>\\n\\n  render() {\\n    const { valid: {info}, submitting } = this.props;\\n    const { getFieldDecorator, getFieldValue } = this.props.form;\\n\\n    const formItemLayout = {\\n      labelCol: {\\n        xs: { span: 24 },\\n        sm: { span: 7 },\\n      },\\n      wrapperCol: {\\n        xs: { span: 24 },\\n        sm: { span: 12 },\\n        md: { span: 10 },\\n      },\\n    };\\n\\n    const submitFormLayout = {\\n      wrapperCol: {\\n        xs: { span: 24, offset: 0 },\\n        sm: { span: 10, offset: 7 },\\n      },\\n    };\\n\\n    return (\\n<PageHeaderLayout title=\\\"<$ pageData.title $>\\\" content=\\\"<$ pageData.desc $>\\\">\\n  <Card bordered={false}>\\n    <Form\\n      onSubmit={this.handleSubmit}\\n      hideRequiredMark\\n      style={{ marginTop: 8 }}\\n    >\\n      <$ getTemplate(pageData, componentData.components) $>\\n\\n      <FormItem {...submitFormLayout} style={{ marginTop: 32 }}>\\n        <% if pageData.confirm %>\\n        <Button type=\\\"primary\\\" htmlType=\\\"submit\\\" loading={submitting}>\\n          <$ pageData.confirm.text $>\\n        </Button>\\n        <% endif %>\\n        <% if pageData.cancel %>\\n        <Button type=\\\"primary\\\" htmlType=\\\"submit\\\" loading={submitting}>\\n          <$ pageData.cancel.text $>\\n        </Button>\\n        <% endif %>\\n        <% for btn in pageData.customBtns %>\\n          <Button type=\\\"reset\\\" onClick={<$ \'this.\' + btn.key + \'Handle\' $>} htmlType=\\\"submit\\\" loading={submitting}>\\n            <$ btn.text $>\\n          </Button>\\n        <% endfor %>\\n      </FormItem>\\n    </Form>\\n  </Card>\\n</PageHeaderLayout>\\n    );\\n  }\\n}\\n\",\"js_template\":null,\"css_template\":null,\"ext_templates\":null,\"user_id\":1,\"created_at\":\"2018-03-04 13:34:54\",\"updated_at\":\"2018-03-04 17:17:14\",\"extra_field\":[{\"id\":10,\"template_id\":13,\"name\":\"tt\",\"label\":\"tt\",\"desc\":\"t\",\"type\":\"1\",\"options\":\"[]\",\"default_value\":\"tthhhhfffj75565\",\"sort\":\"0\",\"created_at\":\"2018-03-04 17:17:14\",\"updated_at\":\"2018-03-04 17:17:14\"},{\"id\":11,\"template_id\":13,\"name\":\"qq\",\"label\":\"qq\",\"desc\":\"qq\",\"type\":\"1\",\"options\":\"[]\",\"default_value\":\"qqhhhhffaa7333\",\"sort\":\"1\",\"created_at\":\"2018-03-04 17:17:14\",\"updated_at\":\"2018-03-04 17:17:14\"}],\"isValid\":true,\"validTipPos\":\"\",\"inter_id\":\"\",\"fields\":[{\"name\":\"name\",\"value\":\"\",\"label\":\"\",\"placeholder\":\" \",\"type\":\"\",\"default_value\":\"@name\",\"rules\":[],\"key\":\"1b0ea957-e47f-4a2b-a2e1-8a577fdc9a93\"},{\"name\":\"email\",\"value\":\"\",\"label\":\"\",\"placeholder\":\"\",\"type\":\"\",\"default_value\":\"@email\",\"rules\":[],\"key\":\"7eb2c8e9-401b-4ba0-83d9-427811d56c85\"},{\"name\":\"time\",\"value\":\"\",\"label\":\"\",\"placeholder\":\"\",\"type\":\"\",\"default_value\":\"@datetime\",\"rules\":[],\"key\":\"21f833ca-94c8-4f0a-9725-11df4572ab08\"},{\"name\":\"sex\",\"value\":\"\",\"label\":\"\",\"placeholder\":\"\",\"type\":\"\",\"default_value\":\"@datetime\",\"rules\":[],\"key\":\"e2e6a991-6241-494d-b1d4-c4ce09a97ab4\"},{\"name\":\"age\",\"value\":\"\",\"label\":\"\",\"placeholder\":\"\",\"type\":\"\",\"default_value\":\"@datetime\",\"rules\":[],\"key\":\"85e4e472-e07c-4dab-983b-eb2a70289bf3\"}],\"tt\":\"tto\",\"qq\":\"qqi\"}', '0', '2018-03-04 21:29:46', '2018-03-04 21:29:46');
