INSERT INTO `template` VALUES ('9', '1', 'table', '表格', '列表模版', '1', 'table import React, { PureComponent, Fragment } from \"react\";\nimport { connect } from \"dva\";\nimport { Link } from \"react-router-dom\";\nimport moment from \"moment\";\nimport {\n  Popconfirm,\n  Row,\n  Col,\n  Card,\n  Form,\n  Input,\n  Select,\n  Icon,\n  Button,\n  Dropdown,\n  Menu,\n  InputNumber,\n  DatePicker,\n  Modal,\n  message,\n  Badge,\n  Divider,\n  Table\n} from \"antd\";\nimport StandardTable from \"../../components/StandardTable\";\nimport PageHeaderLayout from \"../../layouts/PageHeaderLayout\";\n\nconst { Column, ColumnGroup } = Table;\nconst FormItem = Form.Item;\nconst { Option } = Select;\nconst { TextArea } = Input;\n\nconst formItemLayout = {\n  labelCol: {\n    xs: { span: 24 },\n    sm: { span: 3 },\n  },\n  wrapperCol: {\n    xs: { span: 24 },\n    sm: { span: 12 },\n    md: { span: 10 },\n  },\n};\n\nconst submitFormLayout = {\n  wrapperCol: {\n    xs: { span: 24, offset: 0 },\n    sm: { span: 10, offset: 3 },\n  },\n};\n\nconst formItemLayoutFull = {\n  labelCol: {\n    xs: { span: 24 },\n    sm: { span: 3 },\n    sm: { span: 3 },\n  },\n  wrapperCol: {\n    xs: { span: 24 },\n    sm: { span: 21 },\n    md: { span: 21 },\n  },\n};\n\n\n@connect(({ common, loading }) => ({\n  common,\n  // loading: loading.models.common,\n  loading: common.loading,\n}))\n@Form.create()\nexport default class List extends PureComponent {\n  state = {\n\n    selectedRows: [],\n    formValues: {},\n  };\n\n  constructor(props){\n    super(props);\n   \n  }\n\n  componentDidMount() {\n    const { dispatch } = this.props;\n    dispatch({\n      type: \'common/list\',\n    });\n  }\n\n  handleStandardTableChange = (pagination, filtersArg, sorter) => {\n    const { dispatch } = this.props;\n    const { formValues } = this.state;\n\n    const filters = Object.keys(filtersArg).reduce((obj, key) => {\n      const newObj = { ...obj };\n      return newObj;\n    }, {});\n\n    const params = {\n      currentPage: pagination.current,\n      pageSize: pagination.pageSize,\n      ...formValues,\n      ...filters,\n    };\n    if (sorter.field) {\n      params.sorter = `${sorter.field}_${sorter.order}`;\n    }\n\n    dispatch({\n      type: \'common/list\',\n      payload: params,\n    });\n  }\n\n  handleFormReset = () => {\n    const { form, dispatch } = this.props;\n    form.resetFields();\n    this.setState({\n      formValues: {},\n    });\n    dispatch({\n      type: \'common/list\',\n      payload: {},\n    });\n  }\n\n  deleteSelectRows = (e) => {\n    const { dispatch } = this.props;\n    const { selectedRows } = this.state;\n\n    if (!selectedRows) return;\n\n    dispatch({\n      type: \'common/remove\',\n      payload: {\n        id: selectedRows.map(row => row.id),\n      },\n      callback: () => {\n        this.setState({\n          selectedRows: [],\n        });\n      },\n    });\n  }\n\n  handleSelectRows = (rows) => {\n    this.setState({\n      selectedRows: rows,\n    });\n  }\n\n  handleSearch = (e) => {\n    e.preventDefault();\n\n    const { dispatch, form } = this.props;\n\n    form.validateFields((err, fieldsValue) => {\n      if (err) return;\n\n      const values = {\n        ...fieldsValue,\n        updatedAt: fieldsValue.updatedAt && fieldsValue.updatedAt.valueOf(),\n      };\n\n      this.setState({\n        formValues: values,\n      });\n\n      dispatch({\n        type: \'common/list\',\n        payload: values,\n      });\n    });\n  }\n\n  onDelete = (item, index) => {\n    const {dispatch} = this.props;\n    dispatch({\n      type: \'common/remove\',\n      payload: {\n        id: [item.id],\n      },\n      callback: () => {\n        message.success(\'删除成功\');\n      },\n    });\n  }\n\n\n  renderSimpleForm() {\n    const { getFieldDecorator } = this.props.form;\n    return (\n      <Form onSubmit={this.handleSearch} layout=\"inline\">\n        <Row gutter={{ md: 8, lg: 24, xl: 48 }}>\n          <Col md={8} sm={24}>\n            <FormItem label=\"名称\">\n              {getFieldDecorator(\'name\')(\n                <Input placeholder=\"请输入\" />\n              )}\n            </FormItem>\n          </Col>\n          <Col md={8} sm={24}>\n            <span>\n              <Button type=\"primary\" htmlType=\"submit\">查询</Button>\n              <Button style={{ marginLeft: 8 }} onClick={this.handleFormReset}>重置</Button>\n            </span>\n          </Col>\n        </Row>\n      </Form>\n    );\n  }\n\n  renderForm() {\n    return this.renderSimpleForm();\n  }\n\n  render() {\n    // console.log(this.props, \'this.props\')\n    const { common: { data }, loading } = this.props;\n    const { getFieldDecorator, getFieldValue } = this.props.form;\n    const { selectedRows, modalVisible } = this.state;\n    const columns = this.columns;\n\n    return (\n      <PageHeaderLayout title=\"<$ pageData.title $>\" content=\"<$ pageData.desc $>\">\n        <Card bordered={false}>\n          <div>\n            <$ renderComponentAll() $>\n            <div>\n              {this.renderForm()}\n            </div>\n            <div>\n              <Link to=\"/common/add/0\">\n                <Button icon=\"plus\" type=\"primary\">\n                  新建\n                </Button>\n              </Link>\n              {\n                selectedRows.length > 0 && (\n                  <span>\n                    <Button onClick={this.deleteSelectRows}>批量删除</Button>\n                  </span>\n                )\n              }\n            </div>	\n            \n              \n             <Table dataSource={data.list}>\n\n                   <$ renderCustomTemplate() $>\n                  <Column\n                    title=\"Action\"\n                    key=\"action\"\n                    render={(text, record) => (\n                      <span>\n                        <a href=\"#\">Action 一 {record.name}</a>\n                        <Divider type=\"vertical\" />\n                        <a href=\"#\">Delete</a>\n                        <Divider type=\"vertical\" />\n                        <a href=\"#\" className=\"ant-dropdown-link\">\n                          More actions <Icon type=\"down\" />\n                        </a>\n                      </span>\n                    )}\n                  />\n            </Table>\n      \n          </div>\n        </Card>\n      </PageHeaderLayout>\n    );\n  }\n}\n', '1', '2018-03-02 01:09:58', '2018-03-23 11:03:00');
INSERT INTO `template` VALUES ('13', '1', 'form', '表单', '表单模版', '1', 'import React, { PureComponent } from \'react\';\nimport { connect } from \'dva\';\nimport { routerRedux } from \'dva/router\';\nimport {\n  message,\n  Checkbox,\n  Form, Input, DatePicker, Select, Button, Card, InputNumber, Radio, Icon, Tooltip,\n} from \'antd\';\nimport PageHeaderLayout from \'../../layouts/PageHeaderLayout\';\n\nconst FormItem = Form.Item;\nconst { Option } = Select;\nconst { RangePicker } = DatePicker;\nconst { TextArea } = Input;\n\n@connect(({ common, loading }) => ({\n  common: common,\n  submitting: loading.effects[\'common/add\'],\n}))\n@Form.create()\nexport default class BasicForms extends PureComponent {\n  componentDidMount(){\n    let id = 123;\n    if(id != 0){\n      this.props.dispatch({\n        type: \'common/info\',\n        payload: {\n          id: id,\n        },\n      });\n    }\n  }\n  handleSubmit = (e) => {\n    e.preventDefault();\n    this.props.form.valiateFieldsAndScroll((err, values) => {\n      if (!err) {\n        this.props.dispatch({\n          type: \'common/add\',\n          payload: values,\n          callback: () => {\n            message.success(\'保存成功\');\n\n            this.props.dispatch(routerRedux.push(\'/common/list\'));\n          }\n        });\n      }\n    });\n  }\n\n  <% for btn in pageTemplateData.customBtns %>\n    <$ btn.key + \'BeforeHandle\' $> = (e) => {\n      <$ btn.before $>\n    }\n    <$ btn.key + \'Handle\' $> = (e) => {\n      if(!this.<$ btn.key + \'BeforeHandle()\' $>) return ;\n\n      <$ btn.cb $>\n\n      this.<$ btn.key + \'AfterHandle();\' $>\n    }\n    <$ btn.key + \'AfterHandle\' $> = (e) => {\n      <$ btn.after $>\n    }\n  <% endfor %>\n\n  render() {\n    const { common: {info}, submitting } = this.props;\n    const { getFieldDecorator, getFieldValue } = this.props.form;\n\n    const formItemLayout = {\n      labelCol: {\n        xs: { span: 24 },\n        sm: { span: 7 },\n      },\n      wrapperCol: {\n        xs: { span: 24 },\n        sm: { span: 12 },\n        md: { span: 10 },\n      },\n    };\n\n    const submitFormLayout = {\n      wrapperCol: {\n        xs: { span: 24, offset: 0 },\n        sm: { span: 10, offset: 7 },\n      },\n    };\n\n    return (\n<PageHeaderLayout title=\"<$ pageData.title $>\" content=\"<$ pageData.desc $>\">\n  <Card bordered={false}>\n    <Form\n      onSubmit={this.handleSubmit}\n      hideRequiredMark\n      style={{ marginTop: 8 }}\n    >\n	    <div>额外字段： label: <$ pageData.extraFieldMap.confirm.label $> name: <$ pageData.extraFieldMap.confirm.name $></div>\n      <$ renderComponentAll() $>\n      <$ renderCustomTemplate() $>\n\n      <FormItem {...submitFormLayout} style={{ marginTop: 32 }}>\n        <Button type=\"primary\" htmlType=\"submit\" loading={submitting}>\n          保存\n        </Button>\n        <Button type=\"primary\" htmlType=\"submit\" loading={submitting} style={{marginLeft: 6}}>\n          取消\n        </Button>\n        <% for btn in pageTemplateData.customBtns %>\n          <Button type=\"reset\" onClick={<$ \'this.\' + btn.key + \'Handle\' $>} htmlType=\"submit\" loading={submitting}>\n            <$ btn.text $>\n          </Button>\n        <% endfor %>\n      </FormItem>\n    </Form>\n  </Card>\n</PageHeaderLayout>\n    );\n  }\n}\n', '1', '2018-03-04 13:34:54', '2018-03-23 11:14:50');

